import React from 'react'
import Logo from '../../assets/Logo/Logo-Full-Light.png'
import { Link } from 'react-router-dom'
import { FaFacebook, FaGoogle, FaTwitter, FaYoutube } from 'react-icons/fa'

const Resources = [
  "Articles",
  "Blog",
  "Chart Sheet",
  "Code challenges",
  "Docs",
  "Projects",
  "Videos",
  "Workspaces",
];

const Footer = () => {
  return (
    <div className='bg-richblack-800'>
      <div className='flex flex-row gap-8 items-center justify-between w-11/12 max-w-maxContent text-richblack-400 mx-auto relative py-14'>
        <div className='border-b w-[100%] flex flex-col pb-5 border-richblack-700'>
          {/* Section 1 */}
            <div className='w-[50%] flex flex-wrap flex-row justify-between boder-r border-richblack-700 pl-3 pr-5 gap-3'>
              <div className='w-[30%] flex flex-col gap-3 mb-7'>
                <img src={Logo} alt="" className='object-contain'/>
                <h1 className='text-richblack-50 font-semibold text-[16px]'>Company</h1>
                <div className='flex flex-col gap-2'>
                  {["About", "Carreers", "Affiliates"].map((ele, i)=>{
                    return(
                      <div key={i} className='
                      text-[14px] cursor-pointer hover:text-richblack-50 transition-all duration-200'>
                        <Link to={ele.toLowerCase}>{ele}</Link>
                      </div>
                    );
                  })}
                </div>
                <div className='flex gap-3 text-lg'>
                  <FaFacebook/>
                  <FaGoogle/>
                  <FaTwitter/>
                  <FaYoutube/>
                </div>
              </div>

              <div className='w-[48%] mb-7'>
                <h1 className='text-richblack-50 font-semibold text-[16px]'>Resources</h1>

                <div className='flex flex-col gap-2 mt-2'>
                  {Resources.map((ele, index)=>{
                    return(
                      <div key={index} className='text-[14px] cursor-pointer hover:text-richblack-50 transition-all duration-200'>
                        <Link to={ele.split(" ").join("-").toLocaleLowerCase}>{ele}</Link>
                      </div>
                    )
                  })}
                </div>
              </div>
            </div>
        </div>
      </div>
    </div>
  )
}

export default Footer
