import React, { useState } from 'react'
import { HomePageExplore } from '../../../data/homepage-explore'
import HighlightText from './HighlightText';
import { PiKeyReturnThin } from 'react-icons/pi';



const tabsName = [
  "Free",
  "New to Coding",
  "Most popular",
  "Skill paths",
  "Career paths",
];

const ExploreMore = () => {

  const [currentTab, setCurrentTab] = useState(tabsName[0]);
  const [courses, setCourses] = useState(HomePageExplore[0].courses);
  const [currentCard, setCurrentCard] = useState(HomePageExplore[0].courses[0].heading);

  const setMyCard = (value) => {
    setCurrentTab(value);
    const result = HomePageExplore.filter((course)=>course.tag === value);
    setCourses(result[0].courses[0].heading);
  }

  return (
    <div>
      {/* Explore more section  */}
      <div>
        <div className='text-4xl font-semibold text-center my-10'>
          Unlock the
          <HighlightText text={"Power of Code"} />
          <p className="text-center text-lg font-semibold text-richblack-300 mt-1">
            Learn to Build Anything You Can Imagine
          </p>
        </div>
      </div>

      {/* Tabs section  */}

      <div className='flex flex-row gap-10 rounded-full bg-richblack-800 mb-5 mt-5 border-richblack-100 px-1 py-1'>
        {tabsName.map((ele, index)=>{
          return(
            <div className={`text-[16px] font-semibold flex flex-row items-center gap-2 ${currentTab === ele ?  "bg-richblack-900 text-richblack-5 font-medium" : "text-richblack-200" } rounded-full transition-all duration-200 cursor-pointer hover:bg-richblack-900 hover:text-richblack-5 px-2 py-2`} key={index} onClick={()=>setMyCard(ele)}>
              {ele}
            </div>

          );
        })}
      </div>

        <div className='lg:h-[150px] '>

          {/* course card ka group  */}

          <div>
            {
              courses.map((element, index)=>{
                return(
                  <CourseCard/>
                )
              })
            }
          </div>
        </div>

    </div>
  )
}

export default ExploreMore
