import React from 'react'
import login from '../assets/Images/login.webp'
import HighlightText from '../components/core/HomePage/HighlightText'

const loginTab = [
  "Student",
  "Instructors",
];

const Login = () => {

  

  return (
    <div>
      <div className='mt-20 mx-auto flex flex-row justify-center items-center gap-20'>
        <div className='flex flex-col gap-2 items-center justify-between'>
          <div className='text-4xl font-semibold text-white'>
            Welcome Back
            <div className='font-semibold text-[16px] text-richblack-400'>
              Build skills for today, tomorrow, and beyond.
              <HighlightText text={"Education"} />
            </div>

            <div className='flex flex-row gap-10 rounded-full bg-richblack-800 mb-5 mt-5 border-richblack-100 px-1 py-1'>
              {loginTab.map((ele, index)=>{
                return (
                  <div
            key={index}
            className={`text-[16px] font-semibold flex flex-row items-center gap-2 ${
              currentTab === ele
                ? "bg-richblack-900 text-richblack-5 font-medium"
                : "text-richblack-200"
            } rounded-full transition-all duration-200 cursor-pointer hover:bg-richblack-900 hover:text-richblack-5 px-2 py-2`}
            onClick={() => setMyCards(ele)} // Update tab and courses
          >
            {ele}
          </div>
                )
              })}
            </div>
          </div>
        </div>
        <div>
          <img src={login} alt="" />
        </div>
      </div>
    </div>
  )
}

export default Login
