import React from 'react'

const CourseCard = ({cardData, currentCard, setCurrentCard}) => {
  return (
    <div className={`w-[360px] w-[30%] ${currentCard === cardData.heading ? "bg-white shadow-[12px_12px_0_0] shadow-yellow-50" : "bg-richblack-800"}
    text-richblack-25 h-[300px] box-border cursor-pointer`}
    onClick={()=>setCurrentCard(cardData?.heading)}>
      <div className='border-b-[2px] border-richblack-400 borfer-dashed  h-[80%] p-6 flex flex-col gap-3'>
        <div className={`${currentCard === cardData?.heading && "text-richblack-800"} font-semibold text-[20px]`}>
          {cardData.heading}
        </div>
      </div>
    </div>
  )
}

export default CourseCard
