import React, { useState } from 'react'
import { HomePageExplore } from '../../../data/homepage-explore'
import HighlightText from './HighlightText';
import { FaArrowRight } from 'react-icons/fa';


const tabsName = [
  "Free",
  "New to Coding",
  "Most popular",
  "Skill paths",
  "Career paths",
];

const ExploreMore = () => {

  const [currentTab, setCurrentTab] = useState(tabsName[0]);
  const [courses, setCourse] = useState(HomePageExplore[0].courses);

  return (
    <div className='flex flex-col al items-center'>
      <div className='text-4xl font-semibold text-center'>
        Unlock the
        <HighlightText text={"Power of Code"} />
      </div>
      <div className="text-center text-lg font-semibold text-richblack-300 mt-1">
        Learn to Build Anything You Can Imagine
      </div>
      
      <div className="group mt-5 p-1 mx-auto rounded-full bg-richblack-800 font-bold text-richblack-200 drop-shadow-[0_1.5px_rgba(255,255,255,0.25)] transition-all duration-200 hover:scale-95 w-fit hover:drop-shadow-none">
            <div className="flex flex-row items-center gap-10 rounded-full px-10 py-[5px] transition-all duration-200 group-hover:bg-richblack-900">
              {tabsName.map((ele, index)=>{
                return(
                  <p key={index}>{ele}</p>
                )
              })}
              
            </div>
          </div>

    </div>
  )
}

export default ExploreMore
