import React from "react";

const CourseCard = ({ index, cardData, currentCard, setCurrentCard }) => {
  const isActive = currentCard === cardData.heading; // Check if the card is active

  return (
    <div
      className={`p-4 rounded-lg shadow-md transition-all duration-200 cursor-pointer ${
        isActive
          ? "bg-whi text-white scale-105"
          : "bg-white text-gray-800 hover:scale-105 hover:shadow-lg"
      }`}
      onClick={() => setCurrentCard(cardData.heading)} // Set the current card on click
    >
      {/* Course Heading */}
      <h3 className="text-lg font-semibold">{cardData.heading}</h3>

      {/* Course Description */}
      <p className="mt-2 text-sm">{cardData.description}</p>

      {/* Additional Information (Optional) */}
      {cardData.additionalInfo && (
        <p className="mt-2 text-xs text-gray-600">{cardData.additionalInfo}</p>
      )}
    </div>
  );
};

export default CourseCard;
